import { Link, useParams } from 'react-router-dom'
import data from '../../LocalJSON/Users.json'


const MessageDetail = () => {

  const { id } = useParams()
  const detailData = data && data.find(something => something.id == id)
  console.log(data)

  return (
    <>
      {/* {detailData.name} */}

      <div className="fluid-container">

        <div className="container">

          <div className="row border-bottom">

            
              <p className='m-0 my-2' style={{ fontSize: '20px', fontWeight: 'bold', color: 'grey' }}> <Link to={`/message/${detailData.id}`}> &#60; </Link></p>
            
         <img src={detailData.pic} alt="" />

              <h5 className='m-0 my-2'>{detailData.name}</h5>

            

          </div>

        </div>



      </div>



    </>
  )
}

export default MessageDetail
