import React,{useState} from 'react'

import { Link, useParams } from 'react-router-dom'
import data from '../../LocalJSON/MessageData.json'

import '../../CSS/MessageDetail.css'


const MessageDetail = () => {

  const { id } = useParams()
  const detailData = data && data.find(something => something.id == id)
  console.log(detailData)

  // const [info1, setInfo1] = useState(0);

  // function incre() {
  //    setInfo1(info1++)
  // }

  return (
    <>
      {/* {detailData.name} */}

      <div className="fluid-container">

        <div className="container">

          <div className="row border-bottom messageDetailHeaderData py-2">

            <p className='m-0 my-2 mr-2' style={{ fontSize: '20px', fontWeight: 'bold', color: 'grey' }}> <Link to={`/message/${detailData.id}`} className='messageDetailHeaderDataBack'> &#60; </Link></p>

            <img src={detailData.pic} alt="" />

            <h5 className='m-0 my-2 ml-2'>{detailData.name}</h5>

          </div>

          {data && data.map((el,index) => {
            console.log(el + "helloo")
            return (


              <div className="row border-bottom">

                <div className="col-12 d-flex justify-content-start">
                  <p>{el.message[index].msg}</p>
                </div>

                <div className="col-12 d-flex justify-content-end">
                  <p>{el.messagee[index].msg}</p>
                </div>
                
              </div>

            )
           
          })}

        </div>



      </div>



    </>
  )
}

export default MessageDetail
